import React from "react";
import axios from "axios";

const BASE_URL =
  "https://my-json-server.typicode.com/lhmisho/jsonserver/themeOnePricingSection";
class Pricing extends React.Component {
  state = {
    data: {},
    pricingData: [],
  };

  componentDidMount() {
    axios
      .get(`${BASE_URL}`)
      .then((res) => {
        this.setState({
          data: res.data,
          pricingData: res.data.pricingData,
        });
        // console.log(this.state)
      })
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <div>
        {/* start pricing */}
        <section id="pricing" className="pricing-section ptb-90">
          <div className="pricing-section-wrap">
            <div className="container">
              <div className="row">
                <div className="col-md-6">
                  <div className="section-heading">
                    <h3>{this.state.initData.heading}</h3>
                    <p>{this.state.initData.content}</p>
                  </div>
                  <div className="panel-group" id="accordion">
                    {/* Start Single Item */}
                    {this.state.data.map((item, idx) => {
                      return (
                        <div key={`faq_${idx}`} className="panel panel-default">
                          <div className="panel-heading" id={`heading_${idx}`}>
                            <h4 className="panel-title">
                              <a
                                data-toggle="collapse"
                                data-parent="#accordion"
                                href={`#collapse_${idx}`}
                                className="collapsed"
                              >
                                {item.question}
                              </a>
                            </h4>
                          </div>
                          <div
                            id={`collapse_${idx}`}
                            className="panel-collapse collapse"
                          >
                            <div className="panel-body">
                              <p>{item.answer}</p>
                            </div>
                          </div>
                        </div>
                      );
                    })}

                    {/* End Single Item */}
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="faq-img">
                    <img
                      src={this.state.initData.image}
                      className="img-responsive"
                      alt=""
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        {/* end pricing */}
      </div>
    );
  }
}

export default Pricing;
