import React, { Component } from 'react'

const initData = {
    heading: "10k+ Experts Trust Us",
    content: "Streamlining different processes and workflows by using user access control Onebook prompts efficiency and productivity levels of an organization, and reduces operating costs."
}

const person = [
    {
        id:1,
        name:  "Beatrice Evans",
        designation:  "Digital Marketer",
        "image": "/img/testimonial-4.png" ,
        "openion": "Professionally grow backward-compatible data without tactical alignments. Completely whiteboard cooperative schemas rather than cross-platform intellectual capital. Quickly benchmark wireless relationships via client-centered models.",
    },
    {
        id:2,
        name:  "Beatrice Evans",
        designation:  "Digital Marketer",
        image: "/img/testimonial-4.png" ,
        openion: "Professionally grow backward-compatible data without tactical alignments. Completely whiteboard cooperative schemas rather than cross-platform intellectual capital. Quickly benchmark wireless relationships via client-centered models.",
    },
    {
        id:3,
        name:  "Beatrice Evans",
        designation:  "Digital Marketer",
        image: "/img/testimonial-4.png" ,
        openion: "Professionally grow backward-compatible data without tactical alignments. Completely whiteboard cooperative schemas rather than cross-platform intellectual capital. Quickly benchmark wireless relationships via client-centered models.",
    },
    {
        id:4,
        name:  "Beatrice Evans",
        designation:  "Digital Marketer",
        image: "/img/testimonial-4.png" ,
        openion: "Professionally grow backward-compatible data without tactical alignments. Completely whiteboard cooperative schemas rather than cross-platform intellectual capital. Quickly benchmark wireless relationships via client-centered models.",
    }
]

class Testimonial extends Component {

    state = {
        data: {},
        person: [],
        content: '“Юу Паркинг Инт” ХХК нь Монгол улсад анхдагч болох Авто зогсоолын түрээсийн цахим peer to peer Uparking платформыг хөгжүүлж байгаа юм. Авто зогсоолын асуудал нь хүрэлцээнээсээ бус зөв менежментгүйгээсээ болж үүсдэг. Тиймээс Uparking платформ нь авто зогсоолын хүрэлцээг нэмэгдүүлэх боломж, цаашид авто зогсоол төлөвлөлтийг нэмэгдүүлэх ач холбогдолтой төсөл гэж харж, хөгжүүлж байна.',
    }

    componentDidMount(){

        // you can also make this by api call
        // axios.get(`${BASE_URL}`)
        //     .then(res => {
        //         this.setState({
        //             data: res.data,
        //             person: res.data.person
        //         })
        //         console.log(this.state)
        //     })
        //     .catch(err => console.log(err))

            this.setState({
                data: initData,
                person: person
            })
    }

    render() {
        return (
            <section className="testimonial-section bg-secondary ptb-90">
                <div className="testimonial-wrap">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="section-heading text-center">
                                    <h3>{this.state.data.heading}</h3>
                                    <p>{this.state.content}</p>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <p>Авто зогсоолын асуудал нь хүрэлцээнээсээ бус зөв менежментгүйгээсээ болж үүсдэг. Тиймээс Uparking платформ нь авто зогсоолын хүрэлцээг нэмэгдүүлэх боломж, цаашид авто зогсоол төлөвлөлтийг нэмэгдүүлэх ач холбогдолтой төсөл гэж харж, хөгжүүлж байна.</p>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default Testimonial
