import React, { Component } from "react";
import axios from "axios";

let bg = "/img/hero-section-bg-2.jpg";
let bgCss = "no-repeat center center fixed";
let bgStyle = {
  background: "url(" + bg + ")" + bgCss,
};

const BASE_URL =
  "https://my-json-server.typicode.com/lhmisho/React-Tutorial/themeOneDownloadSection";

class Download extends Component {
  state = {
    initData: {
      heading: "Get The App Now !",
      content:
        "Seamlessly transform timely e-commerce for diverse leadership skills. Conveniently reconceptualize go forward expertise without extensible applications.Phosfluorescently.",
      image: "/img/download-app.png",
      data: [
        {
          id: 1,
          heaing: "Download On",
          category: "App Store",
          iconClass: "ti-apple",
          extraaClass: "hover-active",
        },
        {
          id: 2,
          heaing: "Git It On",
          category: "Google Play",
          iconClass: "ti-android",
          extraaClass: "",
        },
        {
          id: 3,
          heaing: "Download On",
          category: "Windows",
          iconClass: "ti-microsoft",
          extraaClass: "",
        },
      ],
    },
    data: [],
    image: "/img/download-image.png",
  };
  // componentDidMount(){
  //     axios.get(`${BASE_URL}`)
  //         .then(res => {
  //             this.setState({
  //                 initData: res.data,
  //                 data: res.data.data
  //             })
  //             // console.log(this.state)
  //         })
  //         .catch(err => console.log(err))
  // }

  render() {
    return (
      <section className="download-section" style={bgStyle}>
        <div className="container">
          <div className="row">
            <div className="col-md-5 hidden-sm">
              <div className="download-app-img">
                <img
                  src={this.state.ini.image}
                  alt="app download"
                  className="img-responsive"
                />
              </div>
            </div>
            <div className="col-md-7 col-sm-12">
              <div className="download-app-text">
                <h3>{this.state.initData.heading}</h3>
                <p>{this.state.initData.content}</p>
                <div className="download-app-button">
                  {this.state.data.map((item, idx) => {
                    return (
                      <a
                        key={`dw_${idx}`}
                        href="download"
                        className={`download-btn ${item.extraaClass}`}
                      >
                        <span className={item.iconClass}></span>
                        <p>
                          <small>{item.heaing}</small>
                          <br /> {item.category}
                        </p>
                      </a>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
export default Download;
